import java.util.Scanner;

public class Exercicio05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//Crie um programa que troque os valores 
		//que estão armazenados nas variáveis nome1 e nome2. 
		//A variável nome1 armazenara o valor “mestre do universo”. 
		//A variável nome2 receberá um valor informado pelo usuário.
		//Altere os valores das variáveis 
		//e exiba a soma da variável nome1 com o valor da variável nome2.
		
		Scanner teclado = new Scanner(System.in);
		String nome1 = "mestre do universo";
		String nome2;
		System.out.print("Insira uma frase: ");
		nome2 = teclado.nextLine();
		String nome3 = nome2 + " é " + nome1;
		System.out.println("\n "+nome3);
		teclado.close();
	}

}
